  useEffect(() => {
    const handleCategories = async () => {
      try {
        const response = await axios.get("https://crudapi.co.uk/api/v1/quiz", {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer xqV72-moMK_a_u_QJTHyybjqNfiMlQpZaoyCWPP_St1hs-a3Lw`,
          },
        });

