  return (
    <div className={`${styles.dflex} ${styles.dcolumn}`}>
      {Object.entries(categories).map(([category, quizzes]) => (
        <div key={category} className={`${styles.dflex} ${styles.dcolumn}`}>
          <h2>{category}</h2>
